import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import StatsContainer from './StatsContainer';
import Controls from './Controls';

const API_REMOTO_URL = 'https://proyecto-pas-final.onrender.com/api/historial-entradas';

function Dashboard() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [isOnline, setIsOnline] = useState(true);
  const [filterStartDate, setFilterStartDate] = useState('');
  const [filterEndDate, setFilterEndDate] = useState('');

  const navigate = useNavigate();

  function updateStats(data) {
    const totalEntries = data.length;
    const today = new Date().toDateString();
    const todayEntries = data.filter(entry => {
      return new Date(entry.fecha_hora).toDateString() === today;
    }).length;

    const lastEntry = data.length > 0
      ? new Date(data[data.length - 1].fecha_hora).toLocaleTimeString('es-ES', {
        hour: '2-digit',
        minute: '2-digit'
      })
      : '--:--';

    return { 
      totalEntries, 
      todayEntries, 
      uniqueCards: 0, // No hay UID en datos remotos
      lastEntry 
    };
  }

  async function loadData() {
    setIsLoading(true);
    setError('');
    try {
      const resRemoto = await fetch(API_REMOTO_URL);

      if (!resRemoto.ok) {
        throw new Error('Error al obtener los datos remotos');
      }

      const dataRemoto = await resRemoto.json();
      
      // Ordenar por fecha_hora descendente
      dataRemoto.sort((a, b) => new Date(b.fecha_hora) - new Date(a.fecha_hora));

      setData(dataRemoto);
      setIsOnline(true);
    } catch (err) {
      setError('No se pudieron cargar los datos remotos: ' + err.message);
      setIsOnline(false);
    } finally {
      setIsLoading(false);
    }
  }

  useEffect(() => {
    loadData();
    const reloadInterval = setInterval(loadData, 30000);

    return () => {
      clearInterval(reloadInterval);
    };
  }, []);

  const filteredData = data.filter(entry => {
    if (!filterStartDate && !filterEndDate) return true;
    const entryDate = new Date(entry.fecha_hora);
    if (filterStartDate && entryDate < new Date(filterStartDate)) return false;
    if (filterEndDate) {
      const endDate = new Date(filterEndDate);
      endDate.setHours(23, 59, 59, 999);
      if (entryDate > endDate) return false;
    }
    return true;
  });

  const stats = updateStats(filteredData);

  const handleGoHome = () => {
    navigate('/home');
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-white to-gray-100 p-6 font-[Inter]">
      <div className="max-w-6xl mx-auto">
        {/* Botón de Home en la esquina superior derecha */}
        <div className="flex justify-between items-start mb-4">
          <Header />
          <button
            onClick={handleGoHome}
            className="flex items-center gap-2 bg-purple-600 hover:bg-purple-700 text-white font-semibold px-4 py-2 rounded-lg shadow-md transition-colors duration-300"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
              <path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z" />
            </svg>
            <span>Inicio</span>
          </button>
        </div>

        {/* Controles para filtro de rango de fechas */}
        <div className="flex flex-wrap gap-4 my-4">
          <div className="flex-1 min-w-[200px]">
            <label htmlFor="startDate" className="block font-semibold mb-1 text-gray-700">Fecha inicio:</label>
            <input
              type="date"
              id="startDate"
              value={filterStartDate}
              onChange={e => setFilterStartDate(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            />
          </div>
          <div className="flex-1 min-w-[200px]">
            <label htmlFor="endDate" className="block font-semibold mb-1 text-gray-700">Fecha fin:</label>
            <input
              type="date"
              id="endDate"
              value={filterEndDate}
              onChange={e => setFilterEndDate(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            />
          </div>
          <div className="flex items-end">
            <button
              onClick={() => {
                setFilterStartDate('');
                setFilterEndDate('');
              }}
              className="bg-gray-500 hover:bg-gray-600 text-white font-medium px-4 py-2 rounded-lg transition-colors duration-300"
            >
              Limpiar filtros
            </button>
          </div>
        </div>

        <div className="my-8">
          <StatsContainer
            totalEntries={stats.totalEntries}
            todayEntries={stats.todayEntries}
            uniqueCards={stats.uniqueCards}
            lastEntry={stats.lastEntry}
          />
        </div>

        <div className="mb-6">
          <Controls
            isOnline={isOnline}
            statusMessage={error}
            onRefresh={loadData}
          />
        </div>

        {/* Tabla de datos directamente en el Dashboard */}
        <div className="mb-10 bg-white rounded-lg shadow-md overflow-hidden">
          {error && (
            <div className="text-red-500 p-4 bg-red-50 rounded-lg">{error}</div>
          )}

          {isLoading ? (
            <div className="text-center py-8">Cargando datos...</div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Nombre
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Apellido
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Fecha y Hora
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredData.map((entry, index) => (
                    <tr key={index}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {entry.nombre}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {entry.apellido}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {new Date(entry.fecha_hora).toLocaleString('es-ES')}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
