import React from 'react';
import { useNavigate } from 'react-router-dom';

const LoginSelector = () => {
  const navigate = useNavigate();

  const handleGerenteClick = () => {
    navigate('/gerente-login');
  };

  const handleRemotoClick = () => {
    navigate('/remoto');
  };

  return (
    <div
      className="login-selector-container"
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #f5f7fa 0%, #e4e7eb 100%)',
        padding: '2rem',
      }}
    >
      <h1 style={{ fontSize: '2rem', marginBottom: '2rem', color: '#1f2937' }}>
        Bienvenido, selecciona tu inicio de sesi√≥n
      </h1>

      <div
        style={{
          display: 'flex',
          gap: '2rem',
          flexWrap: 'wrap',
          justifyContent: 'center',
        }}
      >
        {/* Card Gerente */}
        <div
          style={{
            backgroundColor: 'white',
            padding: '2rem',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            width: '250px',
            textAlign: 'center',
          }}
        >
          <h2 style={{ marginBottom: '1rem', color: '#374151' }}>Gerente</h2>
          <button
            onClick={handleGerenteClick}
            style={{
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              cursor: 'pointer',
              borderRadius: '8px',
              backgroundColor: '#4f46e5',
              color: 'white',
              border: 'none',
              transition: 'background 0.3s',
            }}
          >
            Iniciar como Gerente
          </button>
        </div>

        {/* Card Remoto */}
        <div
          style={{
            backgroundColor: 'white',
            padding: '2rem',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            width: '250px',
            textAlign: 'center',
          }}
        >
          <h2 style={{ marginBottom: '1rem', color: '#374151' }}>Remoto</h2>
          <button
            onClick={handleRemotoClick}
            style={{
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              cursor: 'pointer',
              borderRadius: '8px',
              backgroundColor: '#2563eb',
              color: 'white',
              border: 'none',
              transition: 'background 0.3s',
            }}
          >
            Iniciar como Remoto
          </button>
        </div>
      </div>
    </div>
  );
};

export default LoginSelector;
