import React, { useState } from 'react';

function VistaEntrada({ usuario, onLogout }) {
  const [mensaje, setMensaje] = useState('');

  const registrarEntrada = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/registro-entrada', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ usuarioId: usuario.id })
      });
      if (!response.ok) {
        const errorData = await response.json();
        setMensaje('Error al registrar entrada: ' + errorData.error);
      } else {
        const data = await response.json();
        setMensaje('Entrada registrada correctamente. ID: ' + data.id);
      }
    } catch (error) {
      setMensaje('Error en la conexión: ' + error.message);
    }
  };

  return (
    <div style={{ maxWidth: '500px', margin: '20px auto', padding: '20px', border: '1px solid #ccc' }}>
      <h2>Bienvenido, {usuario.nombre} {usuario.apellido}</h2>
      <p>Correo: {usuario.correo}</p>
      <button onClick={registrarEntrada}>Registrar Entrada</button>
      {mensaje && <p>{mensaje}</p>}
      <button onClick={onLogout} style={{ marginTop: '20px' }}>Cerrar Sesión</button>
    </div>
  );
}

export default VistaEntrada;
