import React from 'react';
import { Link } from 'react-router-dom';
import './index.css'; // Asegúrate de importar tu archivo CSS

function HomePage() {
  return (
    <div className="container" style={{ padding: '40px', textAlign: 'center' }}>
      <h1 style={{ fontSize: '2.2em', marginBottom: '30px', color: '#2c3e50' }}>Seleccione una opción</h1>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
        <Link to="/remoto">
          <button className="refresh-btn">Remoto</button>
        </Link>
        <Link to="/fisico">
          <button className="refresh-btn">Físico</button>
        </Link>
        <Link to="/dashboard">
          <button className="refresh-btn">Ver Dashboard</button>
        </Link>
      </div>
    </div>
  );
}

export default HomePage;
