import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import StatsContainer from './StatsContainer';
import Controls from './Controls';

const API_REMOTO_URL = 'https://proyecto-pas-final.onrender.com/api/historial-entradas';

function Dashboard() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [isOnline, setIsOnline] = useState(true);
  const [filterStartDate, setFilterStartDate] = useState('');
  const [filterEndDate, setFilterEndDate] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const navigate = useNavigate();

  function updateStats(data) {
    const totalEntries = data.length;
    const today = new Date().toISOString().split('T')[0];
    const todayEntries = data.filter(entry => {
      return entry.fecha_hora.split('T')[0] === today;
    }).length;

    // Calcular tarjetas únicas
    const uniqueCards = new Set(data.map(entry => entry.uid)).size;

    const lastEntry = data.length > 0
      ? new Date(data[0].fecha_hora).toLocaleTimeString('es-ES', {
          hour: '2-digit',
          minute: '2-digit'
        })
      : '--:--';

    return { 
      totalEntries, 
      todayEntries, 
      uniqueCards,
      lastEntry 
    };
  }

  async function loadData() {
    setIsLoading(true);
    setError('');
    try {
      const resRemoto = await fetch(API_REMOTO_URL);

      if (!resRemoto.ok) {
        throw new Error('Error al obtener los datos remotos');
      }

      const dataRemoto = await resRemoto.json();
      
      // Ordenar por fecha_hora descendente (más reciente primero)
      dataRemoto.sort((a, b) => new Date(b.fecha_hora) - new Date(a.fecha_hora));

      setData(dataRemoto);
      setIsOnline(true);
    } catch (err) {
      setError('No se pudieron cargar los datos remotos: ' + err.message);
      setIsOnline(false);
    } finally {
      setIsLoading(false);
    }
  }

  useEffect(() => {
    loadData();
    const reloadInterval = setInterval(loadData, 30000);

    return () => {
      clearInterval(reloadInterval);
    };
  }, []);

  const filteredData = data.filter(entry => {
    // Filtro por fechas
    if (filterStartDate || filterEndDate) {
      const entryDate = new Date(entry.fecha_hora).toISOString().split('T')[0];
      
      if (filterStartDate && entryDate < filterStartDate) return false;
      if (filterEndDate && entryDate > filterEndDate) return false;
    }

    // Filtro por búsqueda
    if (searchTerm) {
      const searchLower = searchTerm.toLowerCase();
      return (
        entry.nombre.toLowerCase().includes(searchLower) ||
        entry.apellido.toLowerCase().includes(searchLower) ||
        entry.uid.toLowerCase().includes(searchLower)
      );
    }

    return true;
  });

  const stats = updateStats(filteredData);

  const handleGoHome = () => {
    navigate('/home');
  };

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('es-ES', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 p-4 md:p-6 font-[Inter]">
      <div className="max-w-7xl mx-auto">
        {/* Header y botón de inicio */}
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6 gap-4">
          <Header />
          <button
            onClick={handleGoHome}
            className="flex items-center gap-2 bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 text-white font-semibold px-4 py-2 rounded-lg shadow-md transition-all duration-300 hover:shadow-lg"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
              <path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z" />
            </svg>
            <span>Volver al Inicio</span>
          </button>
        </div>

        {/* Controles de filtrado y búsqueda */}
        <div className="bg-white rounded-xl shadow-sm p-4 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div>
              <label htmlFor="startDate" className="block text-sm font-medium text-gray-700 mb-1">Fecha inicio:</label>
              <input
                type="date"
                id="startDate"
                value={filterStartDate}
                onChange={e => setFilterStartDate(e.target.value)}
                className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
              />
            </div>
            <div>
              <label htmlFor="endDate" className="block text-sm font-medium text-gray-700 mb-1">Fecha fin:</label>
              <input
                type="date"
                id="endDate"
                value={filterEndDate}
                onChange={e => setFilterEndDate(e.target.value)}
                className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
              />
            </div>
            <div>
              <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">Buscar:</label>
              <input
                type="text"
                id="search"
                placeholder="Nombre, apellido o UID"
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
              />
            </div>
          </div>
          <div className="flex justify-end gap-3">
            <button
              onClick={() => {
                setFilterStartDate('');
                setFilterEndDate('');
                setSearchTerm('');
              }}
              className="bg-gray-500 hover:bg-gray-600 text-white font-medium px-4 py-2 rounded-lg transition-colors duration-300"
            >
              Limpiar filtros
            </button>
          </div>
        </div>

        {/* Estadísticas */}
        <div className="mb-6">
          <StatsContainer
            totalEntries={stats.totalEntries}
            todayEntries={stats.todayEntries}
            uniqueCards={stats.uniqueCards}
            lastEntry={stats.lastEntry}
          />
        </div>

        {/* Controles de estado */}
        <div className="mb-6">
          <Controls
            isOnline={isOnline}
            statusMessage={error}
            onRefresh={loadData}
          />
        </div>

        {/* Tabla de datos */}
        <div className="bg-white rounded-xl shadow-md overflow-hidden">
          {error && (
            <div className="bg-red-50 text-red-700 p-4 rounded-t-lg flex items-center">
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              {error}
            </div>
          )}

          {isLoading ? (
            <div className="flex justify-center items-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
            </div>
          ) : filteredData.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <p className="mt-2 text-lg font-medium">No se encontraron registros</p>
              <p className="text-sm">Intenta ajustar los filtros de búsqueda</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Nombre
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Apellido
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      UID
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Fecha y Hora
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredData.map((entry, index) => (
                    <tr key={index} className="hover:bg-gray-50 transition-colors">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-gray-900">{entry.nombre}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{entry.apellido}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-mono text-gray-500">{entry.uid}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">
                          {formatDateTime(entry.fecha_hora)}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Paginación (opcional) */}
        {filteredData.length > 0 && (
          <div className="mt-4 flex justify-between items-center bg-white rounded-lg shadow-sm p-4">
            <div className="text-sm text-gray-500">
              Mostrando {filteredData.length} de {data.length} registros
            </div>
            <div className="flex gap-2">
              <button className="px-3 py-1 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50">
                Anterior
              </button>
              <button className="px-3 py-1 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50">
                Siguiente
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
