import React, { useState } from 'react';
import VistaEntrada from './VistaEntrada';

function RemotoPage() {
  const [isRegister, setIsRegister] = useState(true);
  const [formData, setFormData] = useState({
    nombre: '',
    apellido: '',
    correo: '',
    contraseña: '',
    telefono: '',
    fechaNacimiento: '',
    email: '',
    passwordLogin: ''
  });
  const [usuario, setUsuario] = useState(null);
  const [errorLogin, setErrorLogin] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/api/empleados-remotos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          nombre: formData.nombre,
          apellido: formData.apellido,
          correo: formData.correo,
          contraseña: formData.contraseña,
          telefono: formData.telefono,
          fecha_nacimiento: formData.fechaNacimiento
        })
      });
      if (!response.ok) {
        const errorData = await response.json();
        alert('Error al registrar: ' + errorData.error);
      } else {
        const data = await response.json();
        alert('Empleado remoto registrado correctamente: ' + JSON.stringify(data));
      }
    } catch (error) {
      alert('Error en la conexión: ' + error.message);
    }
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setErrorLogin('');
    try {
      const response = await fetch('http://localhost:3000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          correo: formData.email,
          contraseña: formData.passwordLogin
        })
      });
      if (!response.ok) {
        const errorData = await response.json();
        setErrorLogin(errorData.error || 'Error en login');
      } else {
        const data = await response.json();
        setUsuario(data.usuario);
      }
    } catch (error) {
      setErrorLogin('Error en la conexión: ' + error.message);
    }
  };

  const handleLogout = () => {
    setUsuario(null);
    setFormData({
      nombre: '',
      apellido: '',
      correo: '',
      contraseña: '',
      telefono: '',
      fechaNacimiento: '',
      email: '',
      passwordLogin: ''
    });
    setIsRegister(true);
  };

  if (usuario) {
    return <VistaEntrada usuario={usuario} onLogout={handleLogout} />;
  }

  return (
    <div className="container" style={{ maxWidth: '500px', marginTop: '30px' }}>
      {isRegister ? (
        <form onSubmit={handleRegisterSubmit}>
          <div>
            <label>Nombre:</label>
            <input type="text" name="nombre" value={formData.nombre} onChange={handleChange} required />
          </div>
          <div>
            <label>Apellido:</label>
            <input type="text" name="apellido" value={formData.apellido} onChange={handleChange} required />
          </div>
          <div>
            <label>Correo:</label>
            <input type="email" name="correo" value={formData.correo || ''} onChange={handleChange} required />
          </div>
          <div>
            <label>Contraseña:</label>
            <input type="password" name="contraseña" value={formData.contraseña} onChange={handleChange} required />
          </div>
          <div>
            <label>Teléfono:</label>
            <input type="tel" name="telefono" value={formData.telefono} onChange={handleChange} required />
          </div>
          <div>
            <label>Fecha de Nacimiento:</label>
            <input type="date" name="fechaNacimiento" value={formData.fechaNacimiento} onChange={handleChange} required />
          </div>
          <button type="submit">Registrar</button>
          <p style={{ marginTop: '10px' }}>
            ¿Tienes cuenta?{' '}
            <button
              type="button"
              onClick={() => setIsRegister(false)}
              style={{ color: 'blue', textDecoration: 'underline', background: 'none', border: 'none', padding: 0, cursor: 'pointer' }}
            >
              Iniciar sesión
            </button>
          </p>
        </form>
      ) : (
        <form onSubmit={handleLoginSubmit}>
          <div>
            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div>
            <label>Contraseña:</label>
            <input type="password" name="passwordLogin" value={formData.passwordLogin} onChange={handleChange} required />
          </div>
          {errorLogin && <p style={{ color: 'red' }}>{errorLogin}</p>}
          <button type="submit">Iniciar Sesión</button>
        </form>
      )}
    </div>
  );
}

export default RemotoPage;
